// src/components/Debits.js

import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';


class Debits extends Component{
    constructor(props){
        super(props);
        this.state = {
            apiData: {
              id: -1,
              description: "",
              amount: -1,
              date: "0000-00-00T00:00:00.000Z"
            },
            found: false
        }
    }

    handleDebits = async () => {
        let linkToAPI = 'https://moj-api.herokuapp.com/debits';

        try {
            let response = await axios.get(linkToAPI);
            console.log("Data");

            console.log(response.data);
            //below will only execute if API response is sucessful
            //to get actual object from response you need to do
            //response.data
            this.setState({apiData: response.data, found: true});
        } catch (error) {
            if (error.response) {
                /*
                 * The request was made and the server responded with a
                 * status code that falls out of the range of 2xx
                 */
                console.log(error.response.data); //Not Found
                console.log(error.response.status); //404
                this.setState({found: false});
            }

        }
    }

    makeTable = () => {
        let currData = this.state.apiData;
        let table = [];
        table.push(
          <tr key={currData.id}>
            <td>Description: {currData.description}</td>
            <td>Amount: {currData.amount}</td>
            <td>Date: {currData.date}</td>
          </tr>
        );
        return table;
    }


    render(){
        return(
            <div>
                <h1>
                    Debits
                </h1>
                <div>Debits</div>
                <Link to="/">Return to Home</Link>
                { this.state.found 
                ?<div>
                <table id="data">
                    <tbody>
                    {this.makeTable()}
                    </tbody>
                </table>
                </div> 
               : <h4>No results</h4>
            }

            </div>
        );
    }
}

export default Debits;
