import React, {Component} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import Debits from './components/Debits';
import Credits from './components/Credits';

import LogIn from './Login';
import axios from 'axios';

class App extends Component {
  constructor() {
    super();

    this.state = {
      accountBalance: 0,
      currentUser: {
        userName: 'joe_shmo',
        memberSince: '07/23/96',
      },
      debits: [],
      credits: []
    }
  }

  async componentDidMount() {
    let debits = await axios.get("https://moj-api.herokuapp.com/debits")
    let credits = await axios.get("https://moj-api.herokuapp.com/credits")

    debits = debits.data
    credits = credits.data
    let debitSum = 0, creditSum = 0;
    debits.forEach((debit) =>{
      debitSum += debit.amount
    })
    credits.forEach((credit) => {
      creditSum += credit.amount
    })
    let accountBalance = creditSum - debitSum;
    this.setState({debits, credits, accountBalance});
  }

  handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: e.target.value
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    addDebit(input);
  };


  addDebit = (e) => {
    //send to debits view via props
    console.log(e.target.value);
    var date = new Date();
    this.setState({ date });
    debits = ([...debits, {id: "1", description: e.description, amount: e.amount, date: date}]);
  }

  addCredit = (e) => {
    //send to credits view via props
  }

  mockLogIn = (logInInfo) => {
    const newUser = {...this.state.currentUser}
    newUser.userName = logInInfo.userName
    this.setState({currentUser: newUser})
  }

  render() {

    const HomeComponent = () => (<Home accountBalance={this.state.accountBalance}/>);
    const UserProfileComponent = () => (
      <UserProfile userName = {this.state.currentUser.userName} memberSince={this.state.currentUser.memberSince}/>
    );

    const { debits } = this.state;

    const DebitsComponent = () => (
      <div>     
        <Debits addDebit={this.addDebit} debits={debits}/>
          <br/>
            <form onSubmit={this.handleSubmit}>
                <div>Description: </div>
                <input
                    name="description"
                    label="Description"
                    value={description}
                    onChange={this.handleChange}
                />
                <div>Amount: </div>
                <input
                    name="amount"
                    label="Amount"
                    value={amount}
                    onChange={this.handleChange}

                />
                <button type="submit">Submit</button>
            </form>
      </div>

    );

    const { credits } = this.state;

    const CreditsComponent = () => (
     <Credits addCredit={this.addCredit} credits={credits}/>
    );

    const LogInComponent = () => (<LogIn user={this.state.currentUser} mockLogIn={this.mockLogIn}/>)
    return (
        <Router>
          <div>
            <Route exact path="/" render={HomeComponent}/>
            <Route exact path="/userProfile" render={UserProfileComponent}/>
            <Route exact path="/login" render={LogInComponent}/>
            <Route exact path="/debits" render={DebitsComponent}/>
            <Route exact path="/credits" render={CreditsComponent}/>
          </div>
        </Router>
    );
  }


}




export default App;
