const Debits = (props) => {
    let debitsView = () => {
        const { debits } = props;
        return debits.map((debit) =>
        {
            let date = debit.date.slice(0, 10);
            return <li key={debit.id}>{debit.amount} {debit.description} {date}</li>
        })
    }



    return(
        <div>
            <h1>Debits</h1>
            {debitsView()}
            <br/>
            <form>
                <div>Description: </div>
                <input
                    onChange={this.addDebit}
                    name="description"
                    label="Description"
                />
                <div>Amount: </div>
                <input
                    name="amount"
                    label="Amount"
                />
                <button type="submit">Submit</button>
            </form>
        </div>


    )
}

export default Debits;
